import "./navbar.css";
import Button from "../../pages/PokemonDetails/components/button";
import { useState, useEffect } from "react";


export default function Navbar(props){

    const [ info, setInfo] = useState([])

    const number = 20

    useEffect(() =>{

        fetch(`https://pokeapi.co/api/v2/pokemon?limit=${number}`)
        .then((response)=>{
            return response.json()
        })
        .then((info)=>{
            setInfo(info.results)
        })
    },[])


    const capitalizeFirst = str => {
        return str.charAt(0).toUpperCase() + str.slice(1);
      };

    //   const str = pokemon.name;


    const verMas = str => {

        number + 20

      };


    return(

        <>

            <aside className="navbar">

                <h1>Pokédex</h1>



              <ul>

              {info.map((pokemon) => (


            <li><a href={`/${pokemon.name}`}> { capitalizeFirst(pokemon.name)} </a> </li>

            ))}


            </ul>

<Button funcion={verMas} texto="Ver más"></Button>

            </aside>


        </>

    )

}
