import "./pokemonDetails.css"
import { useParams} from "react-router-dom";
import { useState, useEffect } from "react";

export default function PokemonDetails(){

    const pokename = useParams();



    const url = `https://pokeapi.co/api/v2/pokemon/${pokename.name}`;


    const [shiny, setShiny] = useState(false)



    const [ pokemon, setPokemon] = useState([])

    useEffect(() =>{

        fetch(url)
        .then((response)=>{
            return response.json()
        })
        .then((pokemon)=>{
            setPokemon(pokemon)
            console.log(pokemon)
        })
    },[])


    return(
        <>


<button onClick={() => setShiny(!shiny)} > {setShiny === true ? 'ver shiny' : 'ver normal'}  </button>



/* <img src= {setShiny === true ? `${pokemon.sprites.front_shiny }` : `${pokemon.sprites.back_default }`} /> */

<img src={pokemon.sprites.back_default}/>


   <p>{pokemon.name}</p>
   <p>{pokemon.weight}</p>













        </>
    )
}
