import "./pokemonDetails.css"
import { useParams} from "react-router-dom";
import { useState, useEffect } from "react";

export default function PokemonDetails(){

    const pokename = useParams();



    const url = `https://pokeapi.co/api/v2/pokemon/${pokename.name}`;


    const [shiny, setShiny] = useState(false)

    const [isLoading, setIsLoading] = useState(true);
    const [imageUrl, setImageUrl] = useState(null);



    const [ pokemon, setPokemon] = useState([])

    useEffect(() =>{

        fetch(url)
        .then((response)=>{
            return response.json()
        })
        .then((pokemon)=>{
            setPokemon(pokemon)
            console.log(pokemon)
            setImageUrl(pokemon.sprites.back_default); // ⬅️ Guardar datos
            setIsLoading(false); // ⬅️ Desactivar modo "cargando"
        })
    },[])


    return(
        <>
         if (isLoading) { 

        <h1>Cargando...</h1>
         }


    <button onClick={() => setShiny(!shiny)} > {shiny === true ? 'ver normal' : "ver shiny"}  </button>







    <p>{pokemon.name}</p>
    <p>{pokemon.weight}</p>

    {/* <img src= {shiny === true ? `${pokemon.sprites.front_shiny }` : `${pokemon.sprites.back_default }`} />  */}












        </>
    )
}
